-- rendelések létrehozása
CREATE TABLE `sztamas`.`orders` ( `id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(255) NOT NULL , `date` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `email` VARCHAR(255) NOT NULL , `totalPrice` INT NOT NULL , `deliveryPostcode` INT NOT NULL , `deliveryCity` VARCHAR(255) NOT NULL , `deliveryStreet` VARCHAR(255) NOT NULL , `billPostcode` INT NOT NULL , `billCity` VARCHAR(255) NOT NULL , `billStreet` VARCHAR(255) NOT NULL , `comment` VARCHAR(10000) NOT NULL , `completed` BOOLEAN NOT NULL , `completedAt` DATE NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
